#!/bin/bash

# Helper script to start the server with Cloudflare Tunnel for phone access
# This creates a public URL that works from anywhere

echo "🚀 Starting Film Festival Tool with Cloud Tunnel..."
echo ""

# Check if cloudflared is installed
if ! command -v cloudflared &> /dev/null; then
    echo "❌ cloudflared is not installed."
    echo ""
    echo "Install it with:"
    echo "  brew install cloudflare/cloudflare/cloudflared"
    echo ""
    exit 1
fi

# Check if server is already running
if lsof -Pi :3001 -sTCP:LISTEN -t >/dev/null ; then
    echo "✅ Server is already running on port 3001"
else
    echo "📦 Starting server..."
    npm start &
    SERVER_PID=$!
    echo "⏳ Waiting for server to start..."
    sleep 3
fi

echo ""
echo "🌐 Creating cloud tunnel..."
echo ""
echo "📱 Your app will be accessible at the URL shown below"
echo "   Copy this URL and open it on your phone!"
